// screensaver variables
uint32_t anim_timer_scrs    = 0;
uint8_t  current_frame_scrs = 0;
#define ANIM_FRAME_DURATION_SCRS 150

oled_rotation_t oled_init_user(oled_rotation_t rotation) {
    return OLED_ROTATION_270;
}

bool oled_task_user(void) {
    char buffer[6] = {0};

    if (!get_idle_mode()) {
        if (debug_config.enable) oled_write_ln("DEBUG", true);
        /* Host Keyboard Layer Status */
        switch (biton32(layer_state)) {
            case _DVORAK:
                oled_write_ln("BASE ", true);
                break;
            case _LOWER:
                oled_write_ln("LOWER", true);
                break;
            case _RAISE:
                oled_write_ln("RAISE", true);
                break;
            default:
                oled_write_ln("UNDEF", true);
        }
        /* Host Keyboard LED Status */
        led_t led_state = host_keyboard_led_state();
        oled_write("N", led_state.num_lock ? true : false);
        oled_write(" ", false);
        oled_write("C", led_state.caps_lock ? true : false);
        oled_write(" ", false);
        oled_write("S", led_state.scroll_lock ? true : false);
        oled_write_ln("", false);
        oled_write_ln("FAKE", get_fake_mode() ? true : false);
        oled_write_ln("", false);
        oled_write_ln("WPM:", true);
        snprintf(buffer, 6, "%05u", get_current_wpm());
        oled_write_ln(buffer, false);
        render_luna(0, 11);
        dprint("oled normal\n");
    } else {
        dprint("oled idle");
        if (timer_elapsed32(anim_timer_scrs) > ANIM_FRAME_DURATION_SCRS) {
            anim_timer_scrs = timer_read32();
            if (current_frame_scrs < 5) {
                current_frame_scrs++;
            } else {
                current_frame_scrs = 0;
            }

            oled_idle_task(current_frame_scrs);
        }
    }

    return false;
}

